     <script>
          var currentlyVisible = null;

          function show(id) {
               var x = document.getElementById(id);

               if (currentlyVisible) {
                    currentlyVisible.style.display = "none";
               }

               if (x.style.display === "none" || !x.style.display) {
                    x.style.display = "block";
                    currentlyVisible = x;
               } else {
                    x.style.display = "none";
                    currentlyVisible = null;
               }
          }
     </script>

